class EventEmitter{constructor(){this.events={}}on(name,fn){const event=this.events[name];event?event.push(fn):this.events[name]=[fn]}emit(name,argsArr){const event=this.events[name];if(event)for(const listener of event)listener.apply(null,argsArr)}reset(name){this.events[name]=[]}resetAll(){this.events={}}}exports=EventEmitter;